-- title:  nimph
-- author: lzap
-- desc:   inspired by funcade
-- script: lua

-- Playing grid: 15x8 of 16x16 sprites + top bar (240x8)

-- Sprint flags:
-- 0 = start position
-- 1 = finish position
-- 2 = grey ground
-- 7 = solid wall (or body)

total=3600.0 -- total game time until game over
mn=1 -- current level number
mx=0 -- current map x cell
my=0 -- current map y cell
hx=-1 -- head position x
hy=-1 -- head position y
tb=-1 -- blink border until time
hint=nil -- hud hint text
hintStarted=-1 -- hint message time

function BOOT()
    --trace("BOOT")
    --nextLevel()
end

-- find map tile flag
function flagAt(x, y, flag)
    local id = mget(x*2, y*2)
    local rt = fget(id, flag)
    --trace("flagAT x=".. x .." y=".. y .." id="..id.." f="..tostring(flag))
    return rt
end

-- find starting head position via sprite flags
function spritePos(flag)
    for x=mx//2, mx//2+14 do
        for y=my//2, my//2+8 do
            local flag = flagAt(x, y, flag)
            if flag then
                trace("found flag at x="..x.." y="..y.." mx="..mx.." my="..my)
                return x, y
            end
		end
	end
    trace("sprite not found!")
    return 0, 0
end

-- check for remaining ground tiles
function checkGround()
    for x = mx//2, mx//2+14 do
        for y = my//2, my//2+8 do
            local flag = flagAt(x, y, 2)
			if flag then return false end
		end
	end
	return true
end

function startHint(ms,msg)
	hint=msg
	hintStarted = time()+ms
end

function moveh(dx,dy)
	nx=hx-dx
	ny=hy-dy
	-- wall hit
	if flagAt(nx,ny,7) then
		startHint(2000, "(B) to restart")
	else
		mset(0+hx*2,0+hy*2,4)
		mset(0+hx*2,1+hy*2,20)
		mset(1+hx*2,0+hy*2,5)
		mset(1+hx*2,1+hy*2,21)
		hx=nx
		hy=ny
	end
end

function resetLevel()
	trace("Level reset")
	hx=-1
	hy=-1
	hint=nil
	hintStarted=-1
	sync(0,0,false)
end

function nextLevel()
	if checkGround() then
		trace("Ground check complete")
		mn=mn+1
		mx=mx+30
		resetLevel()
		-- TODO: save last level with pmem
	else
		trace("Ground check failed")
		startHint(2000,"(B) to restart")
	end
end

function hintTIC()
	local tm=time()
	if tm > hintStarted then
		hintStarted=-1
	else
		d=string.len(hint)*8
		print(hint,(240-d)//2,0,(tm%3)+2)
	end
end

function hudTIC()
	local tm=string.format("%.1f",total-time()/1000)
	print("Level "..mn,0,0,12)
	print("Time "..tm,240-8*7,0,12)
end

function TIC()
    cls(0)
    map(mx, my, 30, 16, 0, 8)
	if hx == -1 then
        hx, hy = spritePos(0)
	end
	if hx == nil or hy == nil then
		trace("Starting position not found")
		exit()
	end
	if btnp(0,60,6) then moveh(0,1) end
	if btnp(1,60,6) then moveh(0,-1) end
	if btnp(2,60,6) then moveh(1,0) end
	if btnp(3,60,6) then moveh(-1,0) end
	if btnp(5,60,6) then resetLevel() end
    -- draw head
    spr(256, hx % 15 * 16, (hy % 8 * 16) + 8, -1, 1, 0, 0, 2, 2)
	-- level done
	if flagAt(hx,hy,1) then
		nextLevel()
	end
	hudTIC()
	hintTIC()
end

